package com.ns.camel.config;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.Configuration;import org.springframework.context.support.PropertySourcesPlaceholderConfigurer;import org.springframework.context.support.ResourceBundleMessageSource;import org.springframework.transaction.annotation.EnableTransactionManagement;/** * <pre class="code"> * Defines common configurations. * </pre> */@Configuration@ComponentScan(basePackages = { "com.ns.camel" })@EnableTransactionManagementpublic class CommonConfiguration {		@Bean	public static PropertySourcesPlaceholderConfigurer propertySourcesPlaceholderConfigurer() {		return new PropertySourcesPlaceholderConfigurer();	}	@Bean	public ResourceBundleMessageSource messageSource() {		ResourceBundleMessageSource rb = new ResourceBundleMessageSource();		rb.setBasenames(new String[] { "messages/validation" });		return rb;	}}